PUT /gb
{ &quot;mappings&quot;: {
                       &quot;tweet&quot; : {
                                      &quot;properties&quot; : {
                                                              &quot;tweet&quot; : { &quot;type&quot; : &quot;string&quot;, &quot;analyzer&quot;: &quot;english&quot; },
                                                              &quot;date&quot; : { &quot;type&quot; : &quot;date&quot; },
                                                              &quot;name&quot; : { &quot;type&quot; : &quot;string&quot; },
                                                              &quot;user_id&quot; : { &quot;type&quot; : &quot;long&quot; }
                                                             }
                                    }
                     }
 }



