public class IndexerCURD {
    //测试数据，模拟数据库表结构
    private static String ids={&quot;1&quot;,&quot;2&quot;,&quot;3&quot;}; //用户ID
    private static String  names={&quot;kl&quot;,&quot;wn&quot;,&quot;sb&quot;};
    private static String  describes={&quot;shi yi ge mei nan zi&quot;,&quot;Don't know&quot;,&quot;Is an idiot\n&quot;};
