            System.out.println(&quot;当前存储的文档数，包含回收站的文档：:&quot;+reader.maxDoc());
            System.out.println(&quot;回收站的文档数:&quot;+reader.numDeletedDocs());

        } catch (Exception e) {
            e.printStackTrace();
        }
        return reader;
    }

