}

func (stream *SmtpStream) PrepareForNewMessage() {
	logp.Info(&quot;smtp&quot;,&quot;prepare for new message...&quot;)

	stream.data = stream.data[stream.parseOffset:]
	stream.parseOffset = 0
	stream.isClient = false
	stream.message = nil
}



