&quot;filter&quot;: [
&quot;pinyin_filter&quot;
]
}
},
&quot;tokenizer&quot;: {
&quot;my_ngram&quot;: {
&quot;type&quot;: &quot;ngram&quot;,
&quot;min_gram&quot;: 1,
&quot;max_gram&quot;: 50,
&quot;token_chars&quot;: [
&quot;letter&quot;,
&quot;digit&quot;,
&quot;punctuation&quot;,
&quot;symbol&quot;
]
}
},
&quot;filter&quot;: {
